var o=require("@enmity/api/plugin"),e=require("@enmity/components"),n=require("@enmity/api/user");o.registerPlugin({name:"AvatarClone",description:"Set your local profile avatar to someone else's by Discord ID.",version:"1.0.0",authors:["clockwrks"],onStart(){e.Dialog.show({title:"Clone Avatar",body:"Enter Discord User ID:",buttons:[{text:"Set Avatar",onClick:async a=>{const t=await n.getUser(a);if(t&&t.avatar){const r=`https://cdn.discordapp.com/avatars/${a}/${t.avatar}.png`;window.enmity.setLocalAvatar(r)}else e.Dialog.show({title:"Error",body:"Invalid user ID or no avatar found."})}}],input:!0})},onStop(){window.enmity.setLocalAvatar(null)}});
