var n=require("@enmity/api/plugin"),e=require("@enmity/components"),i=require("@enmity/api/user"),r=require("@enmity/api/settings");n.registerPlugin({name:"AvatarClone",description:"Change your local profile avatar to another user's by Discord ID.",version:"1.0.0",authors:["yourName"],settings:r.Settings.section("AvatarClone",[r.Settings.button("Clone Avatar","Set your avatar to someone else's",()=>{e.Dialog.show({title:"Clone Avatar",body:"Enter Discord User ID:",buttons:[{text:"Set Avatar",onClick:async a=>{try{const t=await i.getUser(a);if(t&&t.avatar){const o=`https://cdn.discordapp.com/avatars/${a}/${t.avatar}.png`;window.enmity.setLocalAvatar(o)}else e.Dialog.show({title:"Error",body:"User ID not found or no avatar."})}catch{e.Dialog.show({title:"Error",body:"API error or invalid input."})}}}],input:!0})})]),onStart(){},onStop(){window.enmity.setLocalAvatar(null)}});
